    private void init() {
        setFrameIcon(imageHelper.APP_ICON1);
        CardLayout card = (CardLayout) pnlDoanhThu.getLayout();
        if (imageHelper.USER.isVaiTro()) {
            card.show(pnlDoanhThu, "card1");
        } else {
            card.show(pnlDoanhThu, "card2");
        }

    }

    void fillComboBoxKhoaHoc() {
        DefaultComboBoxModel model = (DefaultComboBoxModel) cboKhoaHoc.getModel();
        model.removeAllElements();
        List<KhoaHoc> list = khdao.select();
        for (KhoaHoc khoaHoc : list) {
            model.addElement(khoaHoc);
        }
        cboKhoaHoc.setSelectedIndex(0);
    }

    void fillComboBoxNam() {
        DefaultComboBoxModel model = (DefaultComboBoxModel) cboNam.getModel();
        model.removeAllElements();
        List<Integer> list = tkdao.getNamKhaiGiang();
        for (Integer nam : list) {
            model.addElement(nam);
        }
        cboNam.setSelectedIndex(0);
    }

    void fillTableBangDiem() {
        DefaultTableModel model = (DefaultTableModel) tblBangDiem.getModel();
        model.setRowCount(0);
        KhoaHoc kh = (KhoaHoc) cboKhoaHoc.getSelectedItem();
        List<Object[]> list = tkdao.getBangDiem(kh.getMaKH());
        for (Object[] row : list) {
            model.addRow(row);
        }

    }

    void fillTableNguoiHoc() {
        DefaultTableModel model = (DefaultTableModel) tblNguoiHoc.getModel();
        model.setRowCount(0);

        List<Object[]> list = tkdao.getNguoiHoc();
        for (Object[] row : list) {
            model.addRow(row);
        }

    }
//Tổng hợp điểm
    void fillTablekhoaHoc() {
        DefaultTableModel model = (DefaultTableModel) tblTongHopDiem.getModel();
        model.setRowCount(0);
        List<Object[]> list = tkdao.getDiemTheoChuyenDe();
        for (Object[] row : list) {
            model.addRow(row);
        }
    }

    void fillTableDoanhThu() {
        DefaultTableModel model = (DefaultTableModel) tblDoanhThu.getModel();
        model.setRowCount(0);
        int nam = Integer.parseInt(cboNam.getSelectedItem().toString());
        List<Object[]> list = tkdao.getDoanhThu(nam);
        for (Object[] row : list) {
            model.addRow(row);
        }
    }

    private void tabsImgae() {
        tabs.setIconAt(0, imageHelper.TAB_NH);
        tabs.setIconAt(1, imageHelper.TAB_BD);
        tabs.setIconAt(2, imageHelper.TAB_THD);
        tabs.setIconAt(3, imageHelper.TAB_DT);

    }

